import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Dashboard() {
  const [text, setText] = useState('');
  const [convertedText, setConvertedText] = useState('');
  const [error, setError] = useState('');

  const handleLogout = () => {
    // Perform logout operation, such as redirecting to the login page or clearing user session
    // Add your logout logic here
  };

  const handleConvert = async () => {
    if (text.trim() === '') {
      // Handle empty input
      return;
    }

    try {
      const response = await axios.post('/convert', { text });
      const { convertedText } = response.data;
      setConvertedText(convertedText);
    } catch (error) {
      setError('Conversion failed.');
      console.error(error);
    }
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('/initial-data');
        const { convertedText } = response.data;
        setConvertedText(convertedText);
      } catch (error) {
        setError('Failed to fetch initial data.');
        console.error(error);
      }
    };

    fetchData();
  }, []);

  return (
    <div className="dashboard">
      <h1 className="dashboard-heading">Welcome to the Dashboard</h1>
      <button className="logout-button" onClick={handleLogout}>
        Logout
      </button>
      <br />
      <textarea
        className="input-textarea"
        value={text}
        onChange={(e) => setText(e.target.value)}
        placeholder="Paste or type your text here..."
        rows="5"
      ></textarea>
      <button className="convert-button" onClick={handleConvert}>
        Convert
      </button>
      <br />
      {error && <div className="error">{error}</div>}
      <textarea
        className="display-textarea"
        value={convertedText}
        readOnly
        placeholder="Converted text will appear here"
        rows="10"
      ></textarea>
    </div>
  );
}

export default Dashboard;
