import React, { useState, useEffect } from 'react';

function Convert() {
  const [data, setData] = useState([]);
  const [selectedConversion, setSelectedConversion] = useState(null);

  useEffect(() => {
    fetch('/conversions')
      .then((res) => res.json())
      .then((data) => {
        setData(data);
        console.log(data);
      })
      .catch((error) => console.error(error));
  }, []);

  const handleConversionClick = (conversion) => {
    setSelectedConversion(conversion);
  };

  return (
    <div>
      <div style={{ float: 'left', width: '30%' }}>
        {data.length === 0 ? (
          <p>Loading...</p>
        ) : (
          <ul>
            {data.map((conversion) => (
              <li
                key={conversion.id}
                style={{ cursor: 'pointer' }}
                onClick={() => handleConversionClick(conversion)}
              >
                Conversion {conversion.id}
              </li>
            ))}
          </ul>
        )}
      </div>
      <div style={{ float: 'right', width: '70%' }}>
        {selectedConversion ? (
          <div>
            <p style={{ color: 'white' }}>Input: {selectedConversion.input}</p>
            <p style={{ color: 'white' }}>Output: {selectedConversion.output}</p>
          </div>
        ) : (
          <p>Select a conversion to view details</p>
        )}
      </div>
    </div>
  );
}

export default Convert;
