// Get DOM elements
const logoutButton = document.getElementById('logout-button');
const inputTextarea = document.getElementById('input-textarea');
const convertButton = document.getElementById('convert-button');
const displayTextarea = document.getElementById('display-textarea');

// Add event listener to the logout button
logoutButton.addEventListener('click', () => {
  // Perform logout operation, such as redirecting to the login page or clearing user session
  // Add your logout logic here
});

// Add event listener to the convert button
convertButton.addEventListener('click', async () => {
  const text = inputTextarea.value.trim();

  if (text === '') {
    // Handle empty input
    return;
  }

  // Send the text to the backend for conversion
  const response = await fetch('/convert', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json'
    },
    body: JSON.stringify({ text })
  });

  if (response.ok) {
    const { convertedText } = await response.json();
    displayTextarea.value = convertedText;
  } else {
    displayTextarea.value = 'Conversion failed.';
  }
});
