// Styles Components 
import {StyledTextInput, StyledFormArea, StyledFormButton, StyledLabel, Avatar, StyledTitle, colors, ButtonGroup, ExtraText,
    TextLink,
    CopyrightText
} from './../components/Styles';

import Logo from './../assets/HearMeOutLogo.png';

import React from 'react';

// formik 
import {Formik, Form} from 'formik'; 
import {TextInput} from '../components/FormLib1';
import * as Yup from 'yup'; 

// icons 
import {FiMail, FiLock} from 'react-icons/fi';

// Loader 
import * as Loader from "react-loader-spinner";

function App() {
return <Loader.TailSpin />;
}


const Signup = () => {
return (
    <div>
        <StyledFormArea>
            <Avatar image={Logo} />
            <StyledTitle color={colors.theme} size={30}>
                 ðŸ’¬ Signup ðŸŽ§
            </StyledTitle>
            <Formik
                initialValues={{
                    email: "",
                    password: "", 
                    repeatPassword:"",
                    dateOfBirth:"",
                    name:""
                }}
                validationSchema={
                    Yup.object({
                        email: Yup.string()
                            .email("Invalid email address")
                            .required("Required"),
                        password: Yup.string()
                            .min(8, "Password is too short")
                            .max(30, "Password is too long")
                            .required("Required"),
                            name: Yup.string().required("Required"),
                            dateOfBirth: Yup.date().required("Required"),
                            repeatPassword: Yup.string().required("Required").oneOf([Yup.ref("Password")], "Passwords must match")

                    })}
                onSubmit={(values, {setSubmitting}) => {
                    console.log(values); 
                }}
            >
                {({isSubmitting}) => (
                    <Form>
                        <TextInput 
                            name = "email"
                            type = "text"
                            label = "Email AddressðŸ“§"
                            placeholder = "user@hearmeout.com"
                            icon ={<FiMail />}
                        />

                        <TextInput
                            name = "password"
                            type = "password"
                            label = "PasswordðŸ”"
                            placeholder = "********"
                            icon = {<FiLock />}
                        />

                        <ButtonGroup>
                            {!isSubmitting && <StyledFormButton type="submit">
                            Login</StyledFormButton>}

                            {isSubmitting && (
                                <Loader 
                                    type="ThreeDots"
                                    color={colors.theme}
                                    height={49}
                                    width={100}
                                />

                            )}
                        </ButtonGroup>
                    </Form>
                )}
            </Formik>

            <ExtraText>
                New here ? <TextLink to="/signup"> Signup </TextLink> here 
            </ExtraText>
        </StyledFormArea>
        <CopyrightText>
            All rights reserved &copy; 2023
        </CopyrightText>
    </div>
);
};

export default Signup;
