// Styles Components 
import {StyledTextInput, StyledFormArea, StyledFormButton, StyledLabel, Avatar, StyledTitle, colors, ButtonGroup} from './../components/Styles';

import Logo from './../assets/HearMeOutLogo.png';

import React from 'react';

// formik 
import {Formik, Form} from 'formik'; 
import {TextInput} from '../components/FormLib1';

// icons 
import {FiMail, FiLock} from 'react-icons/fi';



const Login = () => {
    return (
        <div>
            <StyledFormArea>
                <Avatar image={Logo} />
                <StyledTitle color={colors.theme} size={30}>
                     💬 Login 🎧
                </StyledTitle>
                <Formik
                    initialValues={{
                        email: "",
                        password: "", 
                    }}
                    onSubmit={(values, {setSubmitting}) => {
                        console.log(values); 
                    }}
                >
                    {() => (
                        <Form>
                            <TextInput 
                                name = "email"
                                type = "text"
                                label = "Email Address📧"
                                placeholder = "user@hearmeout.com"
                                icon ={<FiMail />}
                            />

                            <TextInput
                                name = "password"
                                type = "password"
                                label = "Password🔐"
                                placeholder = "********"
                                icon = {<FiLock />}
                            />

                            <ButtonGroup>
                                <StyledFormButton type="submit">
                                Login</StyledFormButton>
                            </ButtonGroup>
                        </Form>
                    )}
                </Formik>
            </StyledFormArea>
        </div>
    );
};

export default Login;
